import React, {Component} from 'react';
import {
    Redirect
} from "react-router-dom";


class News extends Component {
    state = {
        is_login:false
    };

    getData = () =>{
        setTimeout(()=>{
            this.setState({
                is_login :false
            })
        },1000)
    };

    componentDidMount() {
        this.getData()
    }

    render() {
        return (
            <div>
                <h2>News页面</h2>
                {
                    this.state.is_login === false && (
                        <Redirect to={'user'} />
                    )
                }
            </div>
        );
    }
}

export default News;
